import { Link, useNavigate } from "react-router-dom";
import {useState, useEffect} from "react";
import Header from '../components/Header.js';

const url = 'http://34.124.232.186:5000/admin/users/';

function User( { user } ) {

    const [users, setUsers] = useState(null);
    const [currentUser, setCurrentUser] = useState(null);

    const containerStyle = {
        display: 'flex',
        flexDirection: 'row'
    }

    const buttonStyle = {
        backgroundColor: '#80a1e8',
        color: 'white',
        padding: 10,
        margin: 10,
        border: 'none',
        borderRadius: '5px',
        cursor: 'pointer',
    };

    const itemStyle = {
        flex: 1,
        textAlign: 'center',
        fontSize: '32px',
        borderStyle: 'solid',
        borderColor: 'grey',
        margin: 15
    }

    useEffect(() => {
        const fetchData = async () => {
            let data = null;
            try {
                const response = await fetch(url, {
                    method: "GET",
                    headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': "Token " + user.token}});
                console.log(user.token);
                data = await response.json();
                console.log(data);
                setUsers(data.data);
            } catch (error) {
                console.error("Error fetching sample", error);
            } finally {

            }
        };

        if (user.user.is_superuser) {
            fetchData();
        }

        }, []);

    return (
        <div>
            <Header text={'Problem'}/>
            <div style={containerStyle}>
                <div style={itemStyle}>
                    <h1>Users</h1>
                    {users != null ? 
                    (users.map(user => {
                        return <div onClick={() => {setCurrentUser(user);}} style={currentUser.id == user.id ? {borderStyle: 'solid', borderColor: "red", margin: 15} : {borderStyle: 'solid', margin: 15}}>
                            <div style={{display: 'flex', alignItems: 'stretch', fontSize: '20px'}} key={user.id}>
                                <div style={{margin: 5, flex: 1}}>
                                    <p>
                                        Username: {user.username} <br/>
                                        Full name: {user.first_name == null && user.last_name == null ? "None" : user.first_name + user.last_name} <br/>
                                        Email: {user.email}
                                    </p>
                                </div>
                                <div style={{margin: 5, flex: 1}}>
                                    <p>
                                        Verified: {user.is_verified ? "Yes" : "No"} <br/>
                                        Groups: {user.groups.length == 0 ? "None" : user.groups.toString()}
                                    </p>
                                </div>
                                <div style={{margin: 5,  flex: 1}}>
                                    <p>
                                        Last login: {user.last_login == null ? "Never" : user.last_login}<br/>
                                        Problems: {user.solved_problem.length == 0 ? "None" : user.solved_problem.toString()}
                                    </p>
                                </div>
                            </div>
                        </div>
                    })) : null}
                </div>

                <div style={itemStyle}>
                    <h1>User information</h1>
                    <div>
                        <h2>User's Submissions</h2>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default User;
