import { Link, useNavigate } from "react-router-dom";
import {useState, useEffect} from "react";
import Header from '../components/Header.js';
import styles from '../styles.js';
import UserData from './UserData.js';
import ProblemData from './ProblemData.js';

const url = 'http://34.124.232.186:5000/';

function User( { user, type } ) {
    const navigate = useNavigate();

    const [users, setUsers] = useState(null);
    const [currentItem, setCurrentItem] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            let data = null;
            try {
                let toAppend = '';
                if (type == 'user') toAppend = 'admin/users';
                else toAppend = 'problem';
                const response = await fetch(url, {
                    method: "GET",
                    headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': "Token " + user.token}});
                console.log(user.token);
                data = await response.json();
                console.log(data.data);
                setUsers(data.data);
            } catch (error) {
                console.error("Error fetching sample", error);
            } finally {

            }
        };

        if (user != null && user.user.is_superuser) {
            fetchData();
        }

        }, []);

    return (
        <div>
            {user == null ? 
            <div>
                <h1 style={styles.headingStyleApp}>Please log in</h1>
                <div style={styles.buttonContainerStyle}>
                    <button style={styles.buttonStyleApp} onClick={() => navigate("/judge-manager/auth")}>Log in</button>
                </div>
            </div> :
            <div>
                <Header text={type == 'user' ? 'Problem' : 'User'}/>
                <div style={styles.containerStyle}>
                    <div style={{
                                flex: 1,
                                textAlign: 'center',
                                fontSize: '32px',
                                borderStyle: 'solid',
                                borderRadius: 10,
                                borderColor: 'grey',
                                margin: 15}}>
                        <h1>{type == 'user' ? 'Users' : 'Problems'}</h1>
                        {users != null ? 
                        (users.map(user => {
                            return <div onClick={() => setCurrentItem(user)} style={currentItem != null && currentItem.id == user.id ? {borderStyle: 'solid', borderColor: "#2424c7", borderRadius: 10, margin: 15} : {borderStyle: 'solid', borderRadius: 10, margin: 15}}>
                                <p style={{fontSize: '22px', userSelect: 'none'}}>{user.username}</p>
                            </div>
                        })) : null}
                    </div>

                    <div style={{
                                flex: 3,
                                textAlign: 'center',
                                fontSize: '32px',
                                borderStyle: 'solid',
                                borderRadius: 10,
                                borderColor: 'grey',
                                margin: 15}}>
                        {currentItem == null ? (<h1>No item selected</h1>) : (<div>{type == 'user' ? <UserData currentUser={currentItem} /> : <ProblemData currentProblem={currentItem} />}</div>)}


                    </div>
                </div>
            </div>}
        </div>
    );
}

export default User;
