import { Link, useNavigate } from "react-router-dom";
import {useState, useEffect} from "react";
import Header from '../components/Header.js';
import styles from '../styles.js';

function ProblemEditor( { currentProblem, isNew } ) {

    const [problem, setProblem] = useState(currentProblem);

    return (
        <div>
            <h1>{isNew ? "Create new problem" : "Edit problem" }</h1>
            <form style={styles.formStyle}>
                <div>
                    <label htmlFor="author_id">Author ID:</label>
                    <input
                    type="text"
                    id="author_id"
                    placeholder="Enter the author's ID"
                    style={styles.inputStyle}
                    onChange={(e) => {setProblem({...problem, author_id: e.target.value})}}
                    />

                    <label htmlFor="password">Author name:</label>
                    <input
                    type="text"
                    id="author_name"
                    placeholder="Enter the author's name"
                    style={styles.inputStyle}
                    onChange={(e) => {setProblem({...problem, author_name: e.target.value})}}
                    />
                    <label htmlFor="correct_submission">Correct submissions:</label>
                    <input
                    type="text"
                    id="correct_submission"
                    placeholder="Enter the number of correct submissions"
                    style={styles.inputStyle}
                    onChange={(e) => {setProblem({...problem, correct_submission: e.target.value})}}
                    />

                    <label htmlFor="created">Created:</label>
                    <input
                    type="text"
                    id="created"
                    placeholder="When was this created?"
                    style={styles.inputStyle}
                    onChange={(e) => {setProblem({...problem, author_id: e.target.value})}}
                    />
                </div>

                <div>

                </div>

                <button type="button" style={styles.buttonStyle}>Save</button>
            </form>
        </div>
    );
}

export default ProblemEditor;
